mapper.select:
SELECT	*
FROM	RESIDENT
WHERE	RESIDENT_ID = #{residentId}
AND		RESIDENT_PWD = #{residentPwd}
AND		LEAVE_TF = 'N'

mapper.insert:
      INSERT INTO RESIDENT (RESIDENT_SEQ, RESIDENT_ID,RESIDENT_PWD, RESIDENT_NM,
		RESIDENT_PHONE, RESIDENT_EMAIL, APT_DETAIL_INFO_SEQ, HOUSEHOLD_AUTH_TYPE,
		RESIDENT_ENROLL_DT, LEAVE_TF, APT_AUTH_CD, RESIDENT_BIRTH,
		CONFIRM_TF, RESIDENT_GENDER )
      VALUES(
         RESIDENT_SEQ.NEXTVAL, #{residentId}, #{residentPwd}, #{residentNm}, #{residentPhone}
         , #{residentEmail}, #{aptDetailInfoSeq}, #{householdAuthType}, SYSDATE, DEFAULT
         , DEFAULT, #{residentBirth}, DEFAULT, #{residentGender}
      )
